import React, { useRef, useState } from 'react';

const RefEx1 = () => {
  const [count, setCount] = useState(0);
  const countRef = useRef(0);
  let countVar = 0;

  console.log('üé®rendering...');

  const increaseCountState = () => setCount(count + 1);

  const increaseCountRef = () => {
    countRef.current += 1;
    console.log('Ref: ', countRef.current);
  };

  const increaseCountVar = () => {
    countVar += 1;
    console.log('var: ', countVar);
  };

  return (
    <div>
      <h2>state, ref, variableÏùò ÎπÑÍµê</h2>
      <p>state: {count}</p>
      <p>ref: {countRef.current}</p>
      <button onClick={increaseCountState}>State Ïò¨Î†§</button>
      <button onClick={increaseCountRef}>Ref Ïò¨Î†§</button>
      <button onClick={increaseCountVar}>Var Ïò¨Î†§</button>
      <br />
      <input type='text' />
    </div>
  );
};
export default function UseRef() {
  return (
    <>
      <h1>useRef</h1>
      <RefEx1 />
      <RefEx2 />
    </>
  );
}
