import React, { useEffect, useState } from 'react';

const Timer = props => {
  // ÎßàÏö¥Ìä∏: Ï≤´Î†åÎçîÎßÅ
  // ÏóÖÎç∞Ïù¥Ìä∏: Ïû¨Î†åÎçîÎßÅ
  // Ïñ∏ÎßàÏö¥Ìä∏: ÌôîÎ©¥ÏóêÏÑú ÏÇ¨ÎùºÏßê
  useEffect(() => {
    const timer = setInterval(() => {
      console.log('Ïß∏ÍπçÏß∏Íπç‚è∞');
    }, 1000);

    return () => {
      clearInterval(timer);
      console.log('ÌÉÄÏù¥Î®∏ Ï§ëÏßÄ!üòâ');
    };
  }, []);

  return (
    <div>
      <span>ÏΩòÏÜî ÌÉÄÏù¥Î®∏Î•º ÏãúÏûëÌï©ÎãàÎã§.</span>
    </div>
  );
};

export default function UseEffect() {
  const [timerIsShowed, setTimerIsShowed] = useState(false);
  const handleClickButton = () => setTimerIsShowed(!timerIsShowed);

  return (
    <>
      <h1>useEffect</h1>
      {timerIsShowed && <Timer />}
      <button onClick={handleClickButton}>Toggle Timer</button>
    </>
  );
}
