const Main = props => {
  return (
    <main
      className='content'
      style={{
        backgroundColor: props.isDark ? 'black' : 'white',
        color: props.isDark ? 'white' : 'black',
      }}
    >
      {props.children}님, 좋은 하루 되세요
    </main>
  );
};

export default Main;
