import React, { useReducer, useState } from 'react';

/* 
- reducer: stateÏùÑ ÏóÖÎç∞Ïù¥Ìä∏ÌïòÎäî Ïó≠Ìï†(ÏùÄÌñâ)
- dispatch: state ÏóÖÎç∞Ïù¥Ìä∏Î•º ÏúÑÌïú ÏöîÍµ¨
- action: ÏöîÍµ¨Ïùò ÎÇ¥Ïö©
 */

// üéÅ ÏòàÏ†ú 1
const initialState = 0;
const ACTION_TYPE = {
  DEPOSIT: 'deposit',
  WITHDRAW: 'withdraw',
};
const reducerFn = (state, action) => {
  switch (action.type) {
    case ACTION_TYPE.DEPOSIT:
      return state + action.payload;
    case ACTION_TYPE.WITHDRAW:
      return state - action.payload;
    default:
      return state;
  }
};

const ReducerEx1 = () => {
  const [number, setNumber] = useState(0);
  const [currentState, dispatch] = useReducer(reducerFn, initialState);

  const depositHandler = () => {
    dispatch({ type: ACTION_TYPE.DEPOSIT, payload: number });
  };
  const withdrawHandler = () => {
    dispatch({ type: ACTION_TYPE.WITHDRAW, payload: number });
  };

  return (
    <>
      <strong>- ÎπÑÍµêÏ†Å Í∞ÑÎã®Ìïú ÏòàÏ†ú</strong>
      <p>ÏûîÍ≥†: {currentState}Ïõê</p>
      <input
        type='number'
        value={number}
        onChange={e => setNumber(parseInt(e.target.value))}
        step='1000'
      />
      <button onClick={depositHandler}>ÏòàÍ∏à</button>
      <button onClick={withdrawHandler}>Ï∂úÍ∏à</button>
    </>
  );
};

// üéÅ ÏòàÏ†ú 2
const ReducerEx2 = () => {
  const [name, setName] = useState('');

  return (
    <>
      <strong>- ÎπÑÍµêÏ†Å Î≥µÏû°Ìïú ÏòàÏ†ú</strong>
      <p>Ï¥ù ÌïôÏÉù Ïàò: ?</p>
      <input
        type='text'
        placeholder='Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî'
        value={name}
        onChange={e => setName(e.target.value)}
      />
      <button>Ï∂îÍ∞Ä</button>
    </>
  );
};

export default function UseReducer() {
  return (
    <>
      <h1>useReducer</h1>
      <ReducerEx1 />
      <ReducerEx2 />
    </>
  );
}
