import React from 'react';

const Footer = props => {
  const toggleTheme = () => {
    props.setIsDark(!isDark);
  };
  return (
    <footer
      className='footer'
      style={{
        backgroundColor: props.isDark ? 'black' : 'lightgray',
      }}
    >
      <button className='button' onClick={toggleTheme}>
        Mode Toggle
      </button>
    </footer>
  );
};

export default Footer;
