import React, { useContext } from 'react';
import { ThemeContext } from '../../context/ThemeContext';

const Main = ({ children }) => {
  const { isDark } = useContext(ThemeContext);
  return (
    <main
      className='content'
      style={{
        backgroundColor: isDark ? 'black' : 'white',
        color: isDark ? 'white' : 'black',
      }}
    >
      {children}님, 좋은 하루 되세요
    </main>
  );
};

export default Main;
