export const ACTION_TYPE = {
  ADD: 'ADD',
  DEL: 'DEL',
  STRIKE: 'STRIKE',
};

const initialState = {
  count: 0,
  students: [
    {
      id: Date.now(),
      name: 'Wavy',
      isHere: false,
    },
  ],
};

const reducer = (state, action) => {
  switch (action.type) {
    case ACTION_TYPE.ADD:
      return {
        count: state.count++,
        students: [action.payload, ...state.students],
      };
    case ACTION_TYPE.DEL:
      return {};
    case ACTION_TYPE.STRIKE:
      return {};
    default:
      return state;
  }
};
