import React from 'react';

const Header = props => {
  return (
    <header
      className='header'
      style={{
        backgroundColor: props.isDark ? 'black' : 'lightgray',
        color: props.isDark ? 'white' : 'black',
      }}
    >
      Welcome {props.children}
    </header>
  );
};

export default Header;
